import React from 'react';
import { useSelector } from 'react-redux';
import "./CartItem.scss"

const CartItem = () => {

    const items = useSelector((state) => state.cart);


    return <section>
        <div className="cartItem">
            <img alt="bookImg" src="https://kaboo-shop-test.herokuapp.com/img/uploads/5e7ca6071a384a0ff47f9b0d.png"></img>
            <span class="bookName">Uluslarin düsüsü</span>
            <span class="bookCategory">Ekonomi</span>
            <span class="bookPrice">$47.99</span>
            <div className="cartCounter">
                <button>-</button>
                <input type="number" />
                <button>+</button>
            </div>
            <span class="cartTotal">$47.99</span>
            {/* X ICON */}
            <div class="cartCloseBtn">
                <svg xmlns="http://www.w3.org/2000/svg" class="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12" />
                </svg>
            </div>
        </div>
    </section>;
};

export default CartItem;
