import React, { useState } from 'react';
import "./Sidebar.scss"

const Sidebar = () => {

    const [isCatActive, setIsCatActive] = useState(false)
    const [isPriceActive, setIsPriceActive] = useState(false)



    return <aside>
        <div>
            <div className="sidebarCategories">
                <h3>Categories</h3>
                <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                </svg>
            </div>
            <div className="sidebarPrice">
                <div className="sideBarPriceTop">
                    <h3>Price</h3>
                    <svg onClick={() => setIsPriceActive(!isPriceActive)} xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                    </svg>
                </div>
                {isPriceActive &&
                    <div>
                        <input type="range" min="0" max="100"></input>
                    </div>
                }
            </div>
        </div>
    </aside>;
};

export default Sidebar;
