import axios from 'axios';
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import "./Login.scss"
const Login = () => {

    const [formData, setFormData] = useState({
        username: null,
        password: null,
    })

    const navigate = useNavigate()

    function handleChange(e) {
        setFormData(values => ({ ...values, [e.target.name]: e.target.value }))
    }

    async function handleLogin(e) {
        e.preventDefault()


        const regData = {
            username: formData.username,
            password: formData.password,
        }

        console.log(regData);
        try {
            await axios.post("http://localhost:5000/auth/login", formData)
                .then((res) => console.log(res))
                .catch((err) => console.log(err))

        } catch (err) {
            console.log(err.response.data);
        }


        navigate("/")

    }
    return <div className="loginContainer">
        <h1>Booky</h1>
        <div className="loginLinks">
            <span id="login">Login</span>
            <Link to="/register">
                <p id="register">Register</p>
            </Link>
        </div>
        <form onClick={handleLogin} className="loginForm">
            <input onChange={handleChange} value={formData.username || ""} name="username" type="text" placeholder="Username" />
            <input onChange={handleChange} value={formData.password || ""} name="password" type="password" placeholder="Password" />
            <button type="submit">LOGIN</button>
        </form>
    </div>;;
};

export default Login;
