import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios"

const initialState = {
    items: null,
    isLoading: true,
    error: "",
};

export const fetchItem = createAsyncThunk("fetchItem", async () => {
    await axios.get("http://localhost:5000/books")
        .then(res => {
            return initialState = res.data
        })

})

const itemSlice = createSlice({
    name: "item",
    initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder.addCase(fetchItem.pending, (state, action) => {
            state.loading = true;
            state.error = ""
        })
        builder.addCase(fetchItem.fulfilled, (state, action) => {
            state.loading = false;
            state.items = action.payload
        })
        builder.addCase(fetchItem.rejected, async (state, action) => {
            state.loading = false
            state.error = "ERROR FETCHING ITEM DATA!"
        })

    }
})


export default itemSlice.reducer;
