import axios from 'axios';
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import "./Register.scss"

const Register = () => {

    const [regFormData, setRegFormData] = useState({
        username: "",
        password: "",
        password2: ""
    })

    function handleChange(event) {
        setRegFormData(values => ({ ...values, [event.target.name]: event.target.value }))
    }

    function handleRegister(e) {
        e.preventDefault()
        if (regFormData.password !== regFormData.password2) throw new Error("Password didn't match")

        axios.post("http://localhost:5000/user/register", regFormData)
            .then((res) => console.log(res))
            .catch((err) => console.log(err))

    }



    return <div className="registerContainer">
        <h1>Booky</h1>
        <div className="registerLinks">
            <Link to="/login">
                <p id="login">Login</p>
            </Link>
            <span id="register">Register</span>
        </div>
        <form onSubmit={handleRegister} className="registerForm">
            <input onChange={handleChange} value="username" value={regFormData.username || ""} type="text" placeholder="Username" />
            <input onChange={handleChange} value="password" value={regFormData.password || ""} type="password" placeholder="Password" />
            <input onChange={handleChange} value="password2" value={regFormData.password2 || ""} type="password" placeholder="Enter password again" />
            <button type="submit">REGISTER</button>
        </form>
    </div>;
};

export default Register;
