import React from 'react';
import { useDispatch } from 'react-redux';
import "./CartItem.scss"

const CartItem = ({ props }) => {

    console.log(props);

    return <section>
        <div className="cartItem">
            <img alt="bookImg" src={props.imgURL}></img>
            <span class="bookName">{props.title}</span>
            <span class="bookCategory">{props.category}</span>
            <span class="bookPrice">{props.price}</span>
            <div className="cartCounter">
                <button>-</button>
                <input type="number" />
                <button>+</button>
            </div>
            <span class="cartTotal">$47.99</span>
            {/* X ICON */}
            <div class="cartCloseBtn">
                <svg xmlns="http://www.w3.org/2000/svg" class="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12" />
                </svg>
            </div>
        </div>
    </section>;
};

export default CartItem;
